  def admin_nav_link(label, path, icon: nil, small: false)
    active = current_page?(path)
    size_classes = small ? "text-xs py-1.5" : "text-sm py-2"
    base_classes = "group flex items-center rounded-md px-3 font-medium #{size_classes}"
    active_classes = active ? "bg-gray-100 text-gray-900" : "text-gray-600 hover:bg-gray-50 hover:text-gray-900"

    link_to path, class: "#{base_classes} #{active_classes}" do
      parts = []
      if icon && ICONS[icon]
        icon_classes = active ? "text-gray-500" : "text-gray-400 group-hover:text-gray-500"
        icon_size = small ? "mr-2 h-4 w-4" : "mr-3 h-5 w-5"
        parts << content_tag(:svg, ICONS[icon].html_safe, class: "flex-shrink-0 #{icon_size} #{icon_classes}",
          xmlns: "http://www.w3.org/2000/svg", fill: "none", viewBox: "0 0 24 24", "stroke-width": "1.5", stroke: "currentColor")
      end
      parts << label
      safe_join(parts)
    end
  end
end
